func (r *OpenedxReconciler) ConfigMap4(instance *cachev1.Openedx) *corev1.ConfigMap {
	cm := newConfigMap(instance)
	cm.ObjectMeta.Name = "nginx-config" //Name the ConfigMap

	if cm.Data == nil {
		cm.Data = make(map[string]string)
	}

	cm.Data["_tutor.conf"] = "# Allow long domain names\nserver_names_hash_bucket_size 128;\n\n# Set a short ttl for proxies to allow restarts\nresolver 127.0.0.11 [::1]:5353 valid=10s;\n\n# Configure logging to include scheme and server name\nlog_format tutor '$remote_addr - $remote_user [$time_local] $scheme://$host \"$request\" '\n                 '$status $body_bytes_sent \"$http_referer\" '\n                 '\"$http_user_agent\" \"$http_x_forwarded_for\"';"
	cm.Data["cms.conf"] = "\nupstream cms-backend {\n    server cms:8000 fail_timeout=0;\n}\n\n\n\nserver {\n  listen 80;\n  server_name studio.local.overhang.io;\n\n  \n\n  access_log /var/log/nginx/access.log tutor;\n  client_max_body_size 250M;\n  server_tokens off;\n\n  rewrite ^(.*)/favicon.ico$ /static/images/favicon.ico last;\n\n  location @proxy_to_cms_app {\n    \n    proxy_set_header Host $http_host;\n    proxy_redirect off;\n\n    proxy_pass http://cms-backend;\n  }\n\n  location / {\n    try_files $uri @proxy_to_cms_app;\n  }\n\n  location ~ ^/media/(?P<file>.*) {\n    root /var/www/openedx-media;\n    try_files /$file =404;\n    expires 31536000s;\n  }\n}\n"
	cm.Data["extra.conf"] = "# MinIO public service\nupstream minio-backend {\n    server minio:9000 fail_timeout=0;\n}\n\nserver {\n  listen 80;\n  server_name minio.local.overhang.io;\n\n  \n\n  # Disables server version feedback on pages and in headers\n  server_tokens off;\n \n  client_max_body_size 0;\n\n  location / {\n    \n    proxy_set_header Host $http_host;\n    proxy_redirect off;\n\n    proxy_pass http://minio-backend;\n  }\n}"
	cm.Data["lms.conf"] = "\nupstream lms-backend {\n    server lms:8000 fail_timeout=0;\n}\n\n\n\nserver {\n  listen 80;\n  server_name local.overhang.io preview.local.overhang.io;\n\n  \n\n  access_log /var/log/nginx/access.log tutor;\n  client_max_body_size 4M;\n  server_tokens off;\n\n  rewrite ^(.*)/favicon.ico$ /static/images/favicon.ico last;\n\n  location @proxy_to_lms_app {\n    \n    proxy_set_header Host $http_host;\n    proxy_redirect off;\n\n    proxy_pass http://lms-backend;\n  }\n\n  location / {\n    try_files $uri @proxy_to_lms_app;\n  }\n\n  # /login?next=<any image> can be used by 3rd party sites in <img> tags to\n  # determine whether a user on their site is logged into edX.\n  # The most common image to use is favicon.ico.\n  location /login {\n    if ( $arg_next ~* \"favicon.ico\" ) {\n      return 403;\n    }\n    try_files $uri @proxy_to_lms_app;\n  }\n\n  # Need a separate location for the image uploads endpoint to limit upload sizes\n  location ~ ^/api/profile_images/[^/]*/[^/]*/upload$ {\n    try_files $uri @proxy_to_lms_app;\n    client_max_body_size 1049576;\n  }\n\n  location ~ ^/media/(?P<file>.*) {\n    root /var/www/openedx-media;\n    try_files /$file =404;\n    expires 31536000s;\n  }\n}\n"

	return cm
}
